/* *****************************************AUTHOR Paul CurzonThis program demonstrates  - arrays and loops.   Fill an array using a for loop with 5 everywhere then print it out   ******************************************** */import java.util.*; // import the swing library for I/Oclass fivearray{    /* ***************************************************       Set up an array contianing the number 5  everywhere then print it    */   public static void main (String[] param)    {        int [] fives = initArray(5, 12);        printArray(fives);        System.exit(0);            } // END main            /* ***************************************************       Create and Fill an array of a given size with a given value    */      public static int[] initArray(int initvalue, int size)    {       int[] a = new int[size];       // fill the array with the number given everywhere       for(int i = 0; i < a.length; i++)       {           a[i] = 5;       }                   return a;  // pass whole array back    } // END initArray    /* ***************************************************       Print a given array    */          public static void printArray(int [] a)    {       for(int i = 0; i<a.length; i++)       {           System.out.println(i + ":" + a[i]);       }          } // END printArray} // END class fivearray 